'use strict';

module.exports = grunt => {
	grunt.initConfig({
		jsdoc: {
			dist: {
				src: ['lib/*.js', 'lib/**/*.js'],
				options: {
					configure: 'jsdoc.json',
					destination: 'docs',
				},
			},
		},
		watch: {
			scripts: {
				files: ['lib/*.js', 'lib/**/*.js'],
				tasks: ['jsdoc'],
				options: {
					spawn: false,
				},
			},
		},
	});

	grunt.loadNpmTasks('grunt-jsdoc');
	grunt.loadNpmTasks('grunt-contrib-watch');
};
