'use strict';

const Homey = require('homey');
const WifiApp = require('./../WifiApp');
const OAuth2ClientManager = require('./OAuth2ClientManager');

/**
 * The {@link OAuth2App} class exposes the {@link OAuth2ClientManager} property on the {@link https://apps.developer.athom.com/App.html|HomeyApp} instance, this can potentially be used
 * to OAuth2Clients and {@link OAuth2Account}s. Always call `super.onInit()` when extending from this class.
 * @extends WifiApp
 */
class OAuth2App extends WifiApp {
	onInit() {
		Homey.app.OAuth2ClientManager = new OAuth2ClientManager({ log: this.log.bind(this) });
	}
}

module.exports = OAuth2App;
